  computed: {
    filling() {
      if(window.sessionStorage.settingsFilling != undefined) {
        const fillingConst = JSON.parse(window.sessionStorage.settingsFilling).settingsColour.settingsColour
        
        const fillingLoop = []
        for (let i = 0; i <= 12; i++) {
          const fillingVar = JSON.parse(fillingConst[i])
          fillingLoop.push(fillingVar)
        }
        return fillingLoop
      } else {this.$router.push('/login')}
    },

    stngcrd() {
      if(window.sessionStorage.currentUser != undefined) {
        return this.settingcards.map((item, i) => Object.assign({}, item, this.filling[i]));
      } else {this.$router.push('/login')}
    }
  },

  methods: {
    async getSettingsInfo(name) {
      if(name === 'employees') {
        await this.$store.dispatch('getMyEmployeesManagers')
        await this.$store.dispatch('getMyEmployeesRestaurantManagers')
        await this.$store.dispatch('getMyEmployeesReceptionists')
        await this.$store.dispatch('getMyEmployeesCooks')
        await this.$store.dispatch('getMyEmployeesMaids')
        await this.$store.dispatch('getMyEmployeesWaiters')
      } else if(name === 'roomFund') {
        await this.$store.dispatch('getRoomsCount')
      } else if(name === 'blocks') {
        await this.$store.dispatch('getBlocksInfo')
        await this.$store.dispatch('getRoomsCount')
      }
    },

  }
}
